package basic;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=40,b=30;
		System.out.println("a>b : " +(a>b));
		System.out.println("a<b : " +(a<b));
		System.out.println("a==b : " +(a==b));
		System.out.println("a!=b : " +(a!=b));
	}
}
