package basic;

public class StringAll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Ritika Daphal";
		String str=new String("ritika");
		System.out.println("First String : "+s);
		System.out.println("Second String : "+str);
		System.out.println("Length of s : "+s.length());
		System.out.println("Length of s : "+str.length());
		System.out.println("Uppercase : "+s.toUpperCase());
		System.out.println("Uppercase : "+s.toLowerCase());
	}

}
